mapper.update:
update tag_category
set t_name=#{name}
where t_id = #{id}
  and is_category = false;

update tag_category
set is_delete = true
where t_id = #{id}
  and is_category = false;

mapper.select:
select *
from tag_category
where t_id = #{id}
  and is_category = false;

select *
from tag_category
where t_name = #{name}
  and is_category = false;
